package com.paynet.datatypes;

public class DatatypesDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a=11;
			int b=2;
			
			//add
			int result= a+b;
			System.out.println(result);
			
			//minus
			result= a-b;
			System.out.println(result);
			
			//multiply
			result= a*b;
			System.out.println(result);
			
			//divide
			result= a/b;
			System.out.println(result);
	}

}
